mapper.update:
UPDATE userGroup

	 groupName = #{groupName}, 
	 groupDesc = #{groupDesc},
	 groupPublic = #{groupPublic}

WHERE groupNo=#{groupNo}

mapper.select:
SELECT * FROM userGroup

	 groupNo = #{groupNo} 
	OR groupName = #{groupName} 
	AND groupPublic = #{groupPublic}

SELECT * FROM userGroup AS g
JOIN user AS u ON u.groupNo = g.groupNo
WHERE u.userNo = ${userNo};


SELECT count(*) FROM user
GROUP BY groupNo
having count(*)<6 AND groupNo>0;

mapper.update:
UPDATE user SET
	groupNo = #{groupNo}
WHERE userNo=#{userNo}

UPDATE user SET
	groupNo = -#{groupNo}
WHERE userNo=#{userNo}

UPDATE user SET
groupNo = -groupNo
WHERE userNo=#{userNo}

UPDATE user SET
	groupNo = 0
WHERE userNo=#{userNo}

UPDATE user SET
	groupNo = 0
WHERE groupNo= -#{groupNo}

UPDATE userGroup
SET groupExp = groupExp + ${gainExp}
WHERE groupNo=#{groupNo}

UPDATE userGroup
SET groupExp = groupExp - 10, groupLv = groupLv + 1
WHERE groupNo=#{groupNo}
